<?php$miamail = "info@maroccoviaggi.com";$successo = $_POST['success'];$nome = $_POST['name'];$email = $_POST['email'];$oggetto = $_POST['subject'];$msg = $_POST['message'];$messaggio = "Mittente: " . $nome. "\nMessaggio: " . $msg;$headers = "From: " . $email . "\r\n" . "Reply-To: " . $email;mail($miamail,$oggetto,$messaggio,$headers);header("location: " . $successo);?>